import { useRef, useState } from "react";

//Components

//Icons
import { CiCirclePlus } from "react-icons/ci";

const Menu = () =>{
    const [newFile, setNewFile] = useState();

    const fileRef = useRef();

    const uploadFile = () =>{
        fileRef.current.click();
    }

    const handleChange = (e) =>{
        setNewFile(e.target.files[0]);
    }

    return(
        <div className="menu w-full flex">
            <input onChange={handleChange} className="hidden" ref={fileRef} type="file" />
            <CiCirclePlus onClick={uploadFile} className="text-white text-4xl transition-colors duration-300 hover:text-appleLightBlue hover:cursor-pointer"/>
           
        </div>
    );
}

export default Menu;
