import { useState, useEffect, useRef } from "react";

//Components
import BoxWrapper from "../mill-comps/components/BoxWrapper";

//Icons
import { IoCloudDownloadOutline, IoCloudDownload  } from "react-icons/io5";

const FileTile = ({file}) =>{
    const [displayName, setDisplayName] = useState("");
    const [hovering, setHovering] = useState(false);
    const [isTran, setIsTran] = useState(false);
    const [textClass, setTextClass] = useState("");
    const [btnClass, setBtnClass] = useState("");
 
    const textRef = useRef();
    const btnRef = useRef();

    useEffect(() =>{
        if(file){
            setDisplayName(
                (file.og_name.length < 25) ? file.og_name.length : file.og_name.substring(0, 25) + "..."
            );
        }
    }, [file]);


    //Controls the events when the hover transition finishes
    const onTranFinish = (func) =>{
        setIsTran(true);
        setTimeout(() =>{
            setIsTran(false);
        }, [300]);
        func();
    }

    const startHover = () =>{
        setBtnClass("inline-block");
        setTextClass("hidden");
    }

    const stopHover = () =>{

    }

    useEffect(() =>{
        if(btnRef.current && textRef.current){
            alert('Both refs loaded!');
            btnRef.current.addEventListener('transitionend', (e) =>{
                if (e.propertyName === 'opacity') {
                    // Your code to execute when opacity transition finishes
                    alert('Opacity transition finished!');
                }
            });
        }
    }, [btnRef, textRef]);


    return(
        <div onMouseEnter={() => setHovering(true)} onMouseLeave={() => setHovering(false)}>
            <BoxWrapper className="flex items-center justify-center w-64 h-10 fileTile!bg-appleGray shadow-md !p-2 text-center">
                <p ref={textRef} className={`transition-opacity duration-300 ${hovering && "opacity-0"}`}>{displayName}</p>
                <div><IoCloudDownloadOutline className={`transition-opacity duration-300 hover:cursor-pointer hover:text-appleLightBlue ${!hovering && "opacity-0 "}`} /></div>
                
            </BoxWrapper>
        </div>
    );
}

export default FileTile;
