import { useState, useEffect } from "react";

//Components
import BoxWrapper from "../mill-comps/components/BoxWrapper";

//Icons
import { IoCloudDownloadOutline, IoCloudDownload  } from "react-icons/io5";

const FileTile = ({file}) =>{
    const [displayName, setDisplayName] = useState("");
    const [hovering, setHovering] = useState(false);
    const [textClass, setTextClass] = useState("");
    const [buttonClass, setButtonClass] = useState("");

    useEffect(() =>{
        if(file){
            setDisplayName(
                (file.og_name.length < 25) ? file.og_name.length : file.og_name.substring(0, 25) + "..."
            );
        }
    }, [file]);

    useEffect(() =>{
        if(hovering){
            setTextClass("opacity-0");
            setTimeout(() =>{setTextClass("opacity-0 hidden")}, [300]);
        }else{

        }
    }, [hovering]);

    return(
        <div onMouseEnter={() => setHovering(true)} onMouseLeave={() => setHovering(false)}>
            <BoxWrapper className="flex items-center justify-center w-64 h-10 fileTile!bg-appleGray shadow-md !p-2 text-center">
                <p className={`transition-all duration-300 ${textClass}`}>{displayName}</p>
                <IoCloudDownloadOutline className={`transition-all duration-300 hover:cursor-pointer hover:text-appleLightBlue ${buttonClass}`} />
            </BoxWrapper>
        </div>
    );
}

export default FileTile;
