import {Routes, Route} from 'react-router-dom';
import { useEffect, useState } from "react";
import fetchAll from './functions/fetch';
import { createContext } from 'react';

//Components
import Header from "./components/Header";
import Content from "./components/Content";

export const FileContext = createContext();

function App() {
  const [files, setFiles] = useState([]);

    //On the first render, retrieve all of the users files
    useEffect(() =>{
        const getFiles = async () =>{
            const response = await fetchAll.get('/media', {user: "clark"});
            if(response){
              setFiles(response.data);
            }
        }

        getFiles();
    }, []);

  return (
    <div className="App flex flex-col items-center justify-center bg-deepBlack">
      <FileContext.Provider value={{files, setFiles}}>
        <Header />
        <Routes>
          <Route path='/' element={<Content />}/>
        </Routes>
      </FileContext.Provider>
    </div>
  );
}

export default App;
