                {!hovering ? 
                    <p className="transition-all duration-300">{displayName}</p> :
                    <IoCloudDownloadOutline className="transition-all duration-300 hover:cursor-pointer hover:text-appleLightBlue" />
                }
            </BoxWrapper>
        </div>
    );
}

export default FileTile;
